import React, {Component} from "react";
//class base component
export default class Card extends Component {
  constructor(){
    super();
        this.state = {
            isFlipped: false,
            checkCards: this.props.checkCards()

        }
      }

  showCard=(card)=>{
    this.setState({isFlipped: !this.state.isFlipped}) 
    this.props.clickAction(card);


    // let id = card.id
