  }




  render(){
  return (

    <div className="card" 
    key={this.props.cardData.id} 
    onClick={()=>this.showCard(this.props.cardData)}
    checkCards={this.props.checkCards}

    >

      <div className="card-body">
        {this.state.isFlipped 
        ? <img src={this.props.cardData.frontImg} 
        className="card-img-top" 
        alt={this.props.cardData.pair_name}
        value={this.props.cardData.id} 
        />
        :
        <img src={this.props.cardData.backImg} 
        className="card-img-top" 
        alt={this.props.cardData.pair_name} 
        value={this.props.cardData.id} 

        /> 

        }   
        {/* <h5 className="card-title">{cardData.pair_name}</h5> */}
      </div>
    </div>
      )
    }
  }
